static u8 *FindAsciiDot16(u16 c)
{
	if(c >= ' ' && c <127){
		c -= ' ';
		YaHeiAscii16_9p.Width = AsciiDot16[c][24];
		return (u8 *)AsciiDot16[c];
	}else{
		YaHeiAscii16_9p.Width = 8;
		return (u8 *)FontNotFound;
	}
};

Font_t YaHeiAscii16_9p={
	2,//u8 WidthMin;	//非等宽字体WidthMin!=WidthMax
	12,//u8 WidthMax;	//  等宽字体WidthMin =WidthMax
	6,//u8 WidthDefaul;	//缺省宽度，用于简单计算字符串总宽度
	16,//u8 Height;		//所有字符高度相同
	8, //u8 Width;		//当前查找的字符宽度，在Find函数里更新
	FindAsciiDot16,//u8 * (*Find)(u16 code);	//查找点阵起始位置的函数	
};
