u8 *FindHZ16(u16 code)
{
	int i;
	for(i=0;i<sizeof(HZIndex)/2;i++){
		if(code==HZIndex[i]) return (u8 *)HZDot16[i];;
	}
	return (u8 *)FontNotFound;
}

